import React from 'react';
import propTypes from 'prop-types';
import "./Movie.css";

function Movie({year, title, runtime, summary, poster, genres}) {

    const RUN_H = Math.floor(runtime / 60);
    const RUN_M = runtime % 60;

    return (
        <div className="movies">
            <img onClick={
                function () {
                    const movies__data = document.querySelector(".movies__data");

                    // movies__data.classList.remove("hidden");
                }
            } src={poster} alt={title} title={title}></img>
            <div className="movie__data  .hidden">
                <h5 className="movie__year">{year}</h5>
                <h3 className="movie__title">{title}</h3>
                <ul className="movie__genres">
                    {genres.map((genre, index) => (
                        <li key={index} className="genres__genre">{genre}</li>
                    ))}
                </ul>
                <h4 className="movie__runtime">{RUN_H}h {RUN_M}min</h4>

                <h4>Summary</h4>
                <p className="movie__summary">{summary}</p>
            </div>
        </div>
    ); 
}

Movie.propTypes = {
    id: propTypes.number.isRequired,
    year: propTypes.number.isRequired,
    title: propTypes.string.isRequired,
    runtime: propTypes.number.isRequired,
    summary: propTypes.string.isRequired,
    poster: propTypes.string.isRequired,
    genres: propTypes.arrayOf(propTypes.string).isRequired
}

export default Movie;
